import React, {Component, Fragment} from 'react'

import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import { BrowserRouter as Router, Route, Link, NavLink } from "react-router-dom";
import Button from '@material-ui/core/Button';

const styles = theme => ({
    title: {
        color: '#fff',
        display: 'flex',
        alignItems: 'center',
        margin: '20px 0'
    },
    buttonFavorite: {
        background: 'linear-gradient(to right, rgba(210,255,82,1) 0%, rgba(145,232,66,0.43) 100%)',
        transition: 'all ease .5s',
        color: "#000",
        '&:hover': {
            background: 'linear-gradient(to right, rgba(210,255,82,1) 0%, rgba(145,232,66,0.9) 100%)',
            transition: 'all ease .5s',
        }
    },
    titleText: {
        margin: 0
    }
});

class FavoriteButton extends Component {

    handleAddFavorite(movieId) {
        let arrayFav = []

        if(!this.props.favoritesList){
            console.log('this.props', this.props.movieId, movieId);
            arrayFav.push({id: movieId})
            localStorage.setItem('favorite', JSON.stringify(arrayFav));
        } else {
            arrayFav = this.props.favoritesList
            arrayFav.push({id: movieId})
            console.log('localStorage.getItem("favorite")', this.props);
            console.log('favorit qweqwee', arrayFav);            
            localStorage.setItem('favorite', JSON.stringify(arrayFav));
            this.props.handleChangeFavorites.bind(this)
            this.checkFavorite(this.props.favoritesList)            
        }
        
    }

    checkFavorite(favoritesList){
        if(favoritesList) {
            console.log('itswork!!!!!!!!');
            let finded = favoritesList.find(id=>id.id === this.props.movieId);
            if(finded) {
                return true
            } else {
                return false
            }
        }
    }

    render() {
        const { classes } = this.props;
        
        return (
            !this.checkFavorite(this.props.favoritesList) ? <Button onClick={this.handleAddFavorite.bind(this, this.props.movieId)} className={classes.buttonFavorite}>add to favorite</Button> : ''
        )
    }
}

export default withStyles(styles)(FavoriteButton)
