import React, {Component, Fragment} from 'react'

import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Icon from '@material-ui/core/Icon';

const styles = theme => ({
    background: {
        backgroundRepeat: 'no-repeat',
        minHeight: '100vh',
        backgroundPositionX: 'center',
        backgroundPositionY: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        filter: 'blur(10px)',
        zIndex: '1',
        position:'relative',
        boxShadow: 'inset 0px 0px 32px 0px rgba(0,0,0,0.75)',
    },
    infoWrapper: {
        zIndex: "2",
        maxWidth: 1280,
        width: '100%',
        height: '100%',
        display: 'flex',
        justifyContent: 'space-between',
        margin: '0 auto',
    },
    movieWrapper: {
        width: '100%',
        position: 'absolute',
        zIndex: 2,
        top: 160,
        background: 'rgba(0, 0, 0, .5)',
        minHeight: 'calc(100vh - 80px)'
    },
    moviePageWrapper: {
        overflow: 'hidden',
        background: '#000'
    },
    movieNav: {
        width: '100%',
        height: 80,
        position: 'absolute',
        zIndex: 2,
        top: 80,
        background: 'rgba(0, 0, 0, .5)',
    },
});

class UserMenu extends Component {

    constructor(props) {
        super(props)
        this.state = {
            anchorEl: null,
        };
    }

    handleClick(event) {
        console.log('CLICK',event);
        
        this.setState({ 
            anchorEl: event.currentTarget,
        });
    }
  
    handleClose() {
        this.setState({ 
            anchorEl: null,
        });
        return this.props.history.push(`/user-profile/`)
    }

    render(){
        const { anchorEl } = this.state;
        return(
            <Fragment>
                <Button aria-controls="simple-menu" aria-haspopup="true" onClick={this.handleClick.bind(this)}>
                    <Icon>arrow_drop_down_circle</Icon>
                </Button>
                <Menu
                    id="simple-menu"
                    anchorEl={anchorEl}
                    keepMounted
                    open={Boolean(anchorEl)}
                    onClose={this.handleClose.bind(this)}
                >
                    <MenuItem onClick={this.handleClose.bind(this)}>My account</MenuItem>
                </Menu>
            </Fragment>
        )
    }
}

export default withStyles(styles)(UserMenu)
