import React from 'react';
import CardMovie from './CardMovie';

import { Card, Col, Row } from 'antd';

class CardsListMovie extends React.Component {

    
    constructor(props){
        super(props);

        this.state = {
            data: null
        }
    }

    componentDidMount(){
        return fetch('https://api.themoviedb.org/3/movie/now_playing?api_key=ebea8cfca72fdff8d2624ad7bbf78e4c&language=en-US&page=1', {
                method: 'POST',
                mode: 'cors',
                headers: {
                    'Accept': 'application/json, text/plain, */*',
                    //'Content-Type': 'application/json'
                    "Content-Type": "application/x-www-form-urlencoded; charset=UTF-8" // otherwise $_POST is empty
                },
                body: JSON.stringify({
                })
            })
            .then(response => response.json())
            .then(json => {
                console.log(json);            
                this.setState({
                    data: json
                })
            })
    }

    render(){
        return (
            <Row gutter={16}>
                
                        <Col span={4}>
                            <CardMovie cardInfo={this.state} />
                        </Col>
                }
            </Row>
        )
    }
}

export default CardsListMovie;
