import React, {Component, Fragment} from 'react'

import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Icon from '@material-ui/core/Icon';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';

const styles = theme => ({
    infoWrapper: {
        zIndex: "2",
        maxWidth: 1280,
        width: '100%',
        height: '100%',
        display: 'flex',
        justifyContent: 'space-between',
        margin: '0 auto',
    },
    avatar: {
        margin: 10,
    },
    bigAvatar: {
        margin: 10,
        width: 60,
        height: 60,
    },
});

class UserProfile extends Component {
    render() {
        const { classes } = this.props;
        
        return (
            <Fragment>
                <Grid container className={classes.infoWrapper}>
                    <Grid item xs={2}>
                        <Avatar alt="Remy Sharp" src="https://cdn.pixabay.com/photo/2016/03/31/19/58/avatar-1295429_960_720.png" className={classes.bigAvatar} />
                    </Grid>
                    <Grid item xs={5}>
                        <p>Name: <strong>Hello</strong></p>
                        <p>Surname: <strong>World</strong></p>
                    </Grid>
                    <Grid item xs={5}>
                        
                    </Grid>
                </Grid>
            </Fragment>
        )
    }
}

export default withStyles(styles)(UserProfile)
