import React, {Fragment} from 'react';

import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';

const styles = theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
    },
    itemImg: {
        width: '100%',
        padding: theme.spacing(2),
        textAlign: 'center',
    }
});

class CardMovie extends React.Component {
    render() {
        const { classes } = this.props;
        return (
            <Fragment>
            {
                this.props.movieInfo.map(movie => {
                    return ( 
                        <Grid item xs={2} key={movie.img}>
                            <img className={classes.itemImg} alt={movie.title} src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} />         
                        </Grid>
                    )
                })
            }
            </Fragment>
        )
    }    
}

export default withStyles(styles)(CardMovie);
