import React, {Component, Fragment} from 'react'

import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import { BrowserRouter as Router, Route, Link, NavLink } from "react-router-dom";
import Button from '@material-ui/core/Button';

const styles = theme => ({
    title: {
        color: '#fff',
        display: 'flex',
        alignItems: 'center',
        margin: '20px 0'
    },
    buttonFavorite: {
        background: 'linear-gradient(to right, rgba(210,255,82,1) 0%, rgba(145,232,66,0.43) 100%)',
        transition: 'all ease .5s',
        color: "#000",
        '&:hover': {
            background: 'linear-gradient(to right, rgba(210,255,82,1) 0%, rgba(145,232,66,0.9) 100%)',
            transition: 'all ease .5s',
        }
    },
    titleText: {
        margin: 0
    }
});

class FavoriteButton extends Component {

    handleAddFavorite(movieId) {         
        let favoriteArray = {}
        if(!localStorage.getItem('favorite')){
            console.log('this.props', this.props.movieId, movieId);
            favoriteArray.push({id: movieId})
            localStorage.setItem('favorite', JSON.stringify(favoriteArray));
        } else {
            let favoriteArrayOld = JSON.parse(localStorage.getItem("favorite"));
            
            
            favoriteArrayOld.map(id => {                
                let asd = Object.assign({id: movieId}, id)                
                favoriteArrayOld.push(asd)              
            })

            favoriteArrayOld.push({id: this.props.movieId})
            localStorage.setItem('favorite', JSON.stringify(favoriteArrayOld));
            console.log('favorite', favoriteArrayOld); 
        }
    }

    render() {
        const { classes } = this.props;
        
        return (
            <Button onClick={this.handleAddFavorite.bind(this, this.props.movieId)} className={classes.buttonFavorite}>add to favorite</Button>
        )
    }
}

export default withStyles(styles)(FavoriteButton)
