import React, {Component,Fragment} from 'react'

import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Icon from '@material-ui/core/Icon';
import { BrowserRouter as Router, Route, Redirect, Link, NavLink } from "react-router-dom";
import MoviePoster from './MoviePoster';
import MovieInformation from './MovieInformation'
import Button from '@material-ui/core/Button';

const styles = theme => ({
    background: {
        backgroundRepeat: 'no-repeat',
        minHeight: '100vh',
        backgroundPositionX: 'center',
        backgroundPositionY: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        filter: 'blur(10px)',
        zIndex: '1',
        position:'relative',
        boxShadow: 'inset 0px 0px 32px 0px rgba(0,0,0,0.75)',
    },
    infoWrapper: {
        zIndex: "2",
        maxWidth: 1280,
        width: '100%',
        height: '100%',
        display: 'flex',
        justifyContent: 'space-between',
        margin: '0 auto',
    },
    movieWrapper: {
        width: '100%',
        position: 'absolute',
        zIndex: 2,
        top: 160,
        background: 'rgba(0, 0, 0, .5)',
        minHeight: 'calc(100vh - 80px)'
    },
    moviePageWrapper: {
        overflow: 'hidden',
        background: '#000'
    },
    movieNav: {
        width: '100%',
        height: 80,
        position: 'absolute',
        zIndex: 2,
        top: 80,
        background: 'rgba(0, 0, 0, .5)',
    },
    prevPage: {
        display: 'flex',
        justifyContent: 'flex-start'
    },
    nextPage: {
        display: 'flex',
        justifyContent: 'flex-end'
    },
    prevPageButton: {
        color: 'rgba(255, 255, 255, .5)',
        fontWeight: '300',
        width: '100%',
        textAlign: 'left',
        display: 'flex',
        justifyContent: 'flex-start',
        transition: 'all ease .5s',
        '&:hover': {
            color: 'rgba(255, 255, 255, 1)',
            transition: 'all ease .5s'
        }
    },
    nextPageButton: {
        color: 'rgba(255, 255, 255, .5)',
        fontWeight: '300',
        width: '100%',
        textAlign: 'right',
        display: 'flex',
        justifyContent: 'flex-end',
        transition: 'all ease .5s',
        textDecoration: 'none',
        '&:hover': {
            color: 'rgba(255, 255, 255, 1)',
            transition: 'all ease .5s'
        }
    }
});

class MoviePage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            data: null,
        }
        this.goBack = this.goBack.bind(this);
    }

    getMovieDetails = (movieId) => {
        fetch(`https://api.themoviedb.org/3/movie/${movieId}?api_key=ebea8cfca72fdff8d2624ad7bbf78e4c&language=en-US`, {
                method: 'GET',
                mode: 'cors',
                headers: {
                    'Accept': 'application/json, text/plain, */*',
                    //'Content-Type': 'application/json'
                    "Content-Type": "application/x-www-form-urlencoded; charset=UTF-8" // otherwise $_POST is empty
                },
            })
            .then(response => response.json())
            .then(json => {           
                this.setState({
                    data: json,
                })
            })
    }

    componentDidMount() {
       !!this.props.match.params.id && this.getMovieDetails(this.props.match.params.id);       
    }

    goBack(){
        this.props.history.goBack();
    }

    goNext(id) {        
            let arrElements = this.props.listIDs;
            let currElement = arrElements.indexOf(Number(id));
            let nextElement = 0;                

            if(currElement < arrElements.length - 1) {
                console.log('currElement < arrElements.length', currElement, arrElements.length - 1);                
                nextElement = currElement + 1
            } else {            
                console.log('ELSE', arrElements);                
                this.props.pagesChange(this.props.currPage + 1)
                let arrElements = this.props.listIDs;
                let currElement = arrElements.indexOf(Number(id));
                nextElement = currElement
            }
            console.log('id', id);
            
            return this.props.history.push(`/movie/${arrElements[nextElement]}`)
    }

    render(){
        const { classes } = this.props;
        
        return(
            <div className={classes.moviePageWrapper}>
                <div
                    style={{backgroundImage: "url(" + `https://image.tmdb.org/t/p/w500/${!!this.state.data && this.state.data.backdrop_path}` + ")"}}
                    className={classes.background}
                >
                </div>
                <Grid container className={classes.movieNav}>
                    <Grid item xs={6} className={classes.prevPage}>
                        <Button onClick={this.goBack}  className={classes.prevPageButton}>
                        <Icon>navigate_before</Icon> Back
                        </Button>
                    </Grid>
                    <Grid item xs={6} className={classes.nextPage}>
                        <Button onClick={this.props.match.params.id ? this.getMovieDetails(this.props.match.params.id): undefined} className={classes.nextPageButton}>
                            <div onClick={this.props.listIDs ? this.goNext.bind(this, this.props.match.params.id) : undefined}  className={classes.nextPageButton}>
                                Next <Icon>navigate_next</Icon>
                            </div>
                        </Button>
                    </Grid>
                </Grid>
                <div className={classes.movieWrapper}>
                    <Grid container spacing={10} className={classes.infoWrapper}>
                        {
                            !!this.state.data ? <MoviePoster poster={this.state.data.poster_path} /> : 'Loading...'
                        }
                        {
                            !!this.state.data ? <MovieInformation movieInfo={this.state.data} /> : 'Loading...'
                        }
                    </Grid>
                </div>
            </div>
        )
    }
}

export default withStyles(styles)(MoviePage)
