import React, {Component, Fragment} from 'react'

import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import { BrowserRouter as Router, Route, Link, NavLink } from "react-router-dom";

const styles = theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
    },
    itemWrapp: {
        padding: theme.spacing(2),
        display: 'flex',
        justifyContent: 'center',
    },
    itemImg: {
        width: 292,
        height: 388,
        textAlign: 'center',
        boxShadow: '0px 0px 18px -6px rgba(0,0,0,0.75)',
        transition: 'all ease .3s',
        backgroundPositionX: 'center',
        backgroundPositionY: 'center',
        backgroundSize: '100%',
        backgroundRepeat: 'no-repeat',
        cursor: 'pointer',
        '&:hover': {
            transition: 'all ease .3s',
            backgroundSize: '150%',
            boxShadow: '0px 0px 18px -6px rgba(0,0,0,1)',
        }
    },
    popover: {
        pointerEvents: 'none',
    },
    paper: {
        padding: theme.spacing(1),
    },
});

class MoviePoster extends Component {
    render(){
        const { classes } = this.props;
        console.log(this.props);
        
        return (
                    <Grid item className={classes.posterImage}>
                        <img src={`https://image.tmdb.org/t/p/w500/${this.props.poster}`} />
                    </Grid>
        )
    }
}

export default withStyles(styles)(MoviePoster)
