import React from 'react';
import CardMovie from './CardMovie';

import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';

const styles = theme => ({
    root: {
        flexGrow: 1,
        maxWidth: 1280,
        width: '100%'
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
});

class CardsListMovie extends React.Component {

    constructor(props){
        super(props);

        this.state = {
            spacing: '5',
            data: null,
            page: 1
        }
    }

    componentDidMount() {
        return fetch(`https://api.themoviedb.org/3/movie/now_playing?api_key=ebea8cfca72fdff8d2624ad7bbf78e4c&language=en-US&page=${this.state.page}`, {
                method: 'POST',
                mode: 'cors',
                headers: {
                    'Accept': 'application/json, text/plain, */*',
                    //'Content-Type': 'application/json'
                    "Content-Type": "application/x-www-form-urlencoded; charset=UTF-8" // otherwise $_POST is empty
                },
            })
            .then(response => response.json())
            .then(json => {
                console.log(json);            
                this.setState({
                    data: json
                })
            })
    }

    render(){
        const { classes } = this.props;        
        return (
            <div className={classes.root}>
                <Grid container spacing={0}>
                    {
                        !!this.state.data ? <CardMovie movieInfo={this.state.data.results}/> : 'Loading...'
                    }
                </Grid>
            </div>
        )
    }
}

export default withStyles(styles)(CardsListMovie);
