import React, {Fragment} from 'react';

import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import { relative } from 'path';

const styles = theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
    },
    itemWrapp: {
        padding: theme.spacing(1),
        position: 'relative'   
    },
    itemImg: {
        position: 'absolute',
        width: '90%',
        textAlign: 'center',
        boxShadow: '0px 0px 18px -6px rgba(0,0,0,0.75)',
        transition: 'all ease .3s',
        '&:hover': {
            width: '100%',
            boxShadow: 'none',
            transition: 'all ease .3s'
        }
    }
});

class CardMovie extends React.Component {
    render() {
        const { classes } = this.props;
        return (
            <Fragment>
            {
                this.props.movieInfo.map(movie => {
                    return ( 
                        <Grid item xs={2} className={classes.itemWrapp} key={movie.img}>
                            <img className={classes.itemImg} alt={movie.title} src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} />         
                        </Grid>
                    )
                })
            }
            </Fragment>
        )
    }    
}

export default withStyles(styles)(CardMovie);
