import React, {Component, Fragment} from 'react'

import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Icon from '@material-ui/core/Icon';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import Fab from '@material-ui/core/Fab';

const styles = theme => ({
    background: {
        backgroundRepeat: 'no-repeat',
        minHeight: '100vh',
        backgroundPositionX: 'center',
        backgroundPositionY: 'center',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        filter: 'blur(10px)',
        zIndex: '1',
        position:'relative',
        boxShadow: 'inset 0px 0px 32px 0px rgba(0,0,0,0.75)',
    },
    infoWrapper: {
        zIndex: "2",
        maxWidth: 1280,
        width: '100%',
        height: '100%',
        display: 'flex',
        justifyContent: 'space-between',
        margin: '0 auto',
    },
    movieWrapper: {
        width: '100%',
        position: 'absolute',
        zIndex: 2,
        top: 80,
        background: 'rgba(0, 0, 0, .5)',
        minHeight: 'calc(100vh - 0px)'
    },
    moviePageWrapper: {
        overflow: 'hidden',
        background: '#000'
    },
    avatar: {
        display: 'flex',
        position:'relative',
        width: 180,
        height: 180,
        alignItems: 'center'
    },
    bigAvatar: {
        margin: 10,
        width: 180,
        height: 180,
    },
    fab: {
      margin: theme.spacing(1),
      display: 'none',
      position:'absolute'
    },
    active: {
        display: 'flex'
    }
});

class UserProfile extends Component {

    constructor(props) {
        super(props);
        this.state = {
            activeEditButton: false
        }
    }

    handleEditOpen(){
        this.setState({
            activeEditButton: true
        })
    }

    handleEditClose(){
        this.setState({
            activeEditButton: false
        })
    }

    render() {
        const { classes } = this.props;
        
        return (
            <div className={classes.userPageWrapper}>
                <div
                    style={{backgroundImage: "url(" + `` + ")"}}
                    className={classes.background}
                >
                </div>
                <div className={classes.movieWrapper}>
                    <Grid container spacing={10} className={classes.infoWrapper}>
                        <Grid item xs={2}>
                            <div className={classes.avatar}>
                                <Avatar
                                    onMouseEnter={this.handleEditOpen.bind(this)}
                                    onMouseLeave={this.handleEditClose.bind(this)} 
                                    alt="Remy Sharp" 
                                    src="https://cdn.pixabay.com/photo/2016/03/31/19/58/avatar-1295429_960_720.png" 
                                    className={classes.bigAvatar} 
                                />

                                <Fab size="small" color="secondary" aria-label="Edit" className={`${classes.fab} , ${this.state.activeEditButton ? classes.active : ''}`}>
                                    <Icon>edit_icon</Icon>
                                </Fab>
                            </div>
                        </Grid>
                        <Grid item xs={5}>
                            <p>Name: <strong>Hello</strong></p>
                            <p>Surname: <strong>World</strong></p>
                        </Grid>
                        <Grid item xs={5}>
                            
                        </Grid>
                    </Grid>
                </div>
            </div>
        )
    }
}

export default withStyles(styles)(UserProfile)
