import React from 'react';
import CardMovie from './CardMovie';

import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Pagination from "react-js-pagination";

const styles = theme => ({
    root: {
        maxWidth: 1280,
        width: '100%',
        display: 'flex',
        justifyContent: 'space-between',
        margin: '0 auto'
    },
});

class CardsListMovie extends React.Component {

    constructor(props){
        super(props);

        this.state = {
            spacing: '5',
            data: null,
            page: 1,
            totalPages: 12
        }
    }

    getNowPlayingMovies = (pageNumber) => {
        fetch(`https://api.themoviedb.org/3/movie/now_playing?api_key=ebea8cfca72fdff8d2624ad7bbf78e4c&language=en-US&page=${pageNumber}`, {
                method: 'POST',
                mode: 'cors',
                headers: {
                    'Accept': 'application/json, text/plain, */*',
                    //'Content-Type': 'application/json'
                    "Content-Type": "application/x-www-form-urlencoded; charset=UTF-8" // otherwise $_POST is empty
                },
            })
            .then(response => response.json())
            .then(json => {
                console.log(json);            
                this.setState({
                    data: json
                })
            })
    }

    componentDidMount() {
        this.getNowPlayingMovies(this.state.page);
    }

    handlePageChange(pageNumber) {
        console.log(`active page is ${pageNumber}`);
        this.setState({page: pageNumber});
        this.getNowPlayingMovies(pageNumber);
      }

    render(){
        const { classes } = this.props;        
        return (
            <div>
                <div className={classes.root}>
                    <Grid container spacing={0}>
                        {
                            !!this.state.data ? <CardMovie movieInfo={this.state.data.results}/> : 'Loading...'
                        }
                    </Grid>
                </div>
                <Pagination
                    prevPageText='prev'
                    nextPageText='next'
                    firstPageText='first'
                    lastPageText='last'
                    activePage={this.state.page}
                    itemsCountPerPage={5}
                    totalItemsCount={this.state.totalPages}
                    onChange={this.handlePageChange.bind(this)}
                />
            </div>
        )
    }
}

export default withStyles(styles)(CardsListMovie);
