import React from 'react';
import CardMovie from './CardMovie';

import GridList from '@material-ui/core/GridList';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    root: {
      display: 'flex',
      flexWrap: 'wrap',
      justifyContent: 'space-around',
      overflow: 'hidden',
      backgroundColor: theme.palette.background.paper,
    },
    gridList: {
      width: 500,
      height: 450,
    },
    icon: {
      color: 'rgba(255, 255, 255, 0.54)',
    },
  }));

class CardsListMovie extends React.Component {

    constructor(props){
        super(props);

        this.state = {
            data: null
        }
    }

    componentDidMount() {
        return fetch('https://api.themoviedb.org/3/movie/now_playing?api_key=ebea8cfca72fdff8d2624ad7bbf78e4c&language=en-US&page=1', {
                method: 'POST',
                mode: 'cors',
                headers: {
                    'Accept': 'application/json, text/plain, */*',
                    //'Content-Type': 'application/json'
                    "Content-Type": "application/x-www-form-urlencoded; charset=UTF-8" // otherwise $_POST is empty
                },
            })
            .then(response => response.json())
            .then(json => {
                console.log(json);            
                this.setState({
                    data: json
                })
            })
    }

    render(){
        return (
            <div>
                <GridList cellHeight={160}  cols={3}>
                    {
                        !!this.state.data ? <CardMovie movieInfo={this.state.data.results}/> : 'Loading...'
                    }
                </GridList>
            </div>
        )
    }
}

export default CardsListMovie;
