import React from 'react';
import Header from './Header';
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";

import './App.css';
import CardsListMovie from './CardsListMovie';
import MoviePage from './MoviePage'

class App extends React.Component {

  getNowPlayingMovies = (pageNumber) => {
    fetch(`https://api.themoviedb.org/3/movie/now_playing?api_key=ebea8cfca72fdff8d2624ad7bbf78e4c&language=en-US&page=${pageNumber}`, {
            method: 'GET',
            mode: 'cors',
            headers: {
                'Accept': 'application/json, text/plain, */*',
                //'Content-Type': 'application/json'
                "Content-Type": "application/x-www-form-urlencoded; charset=UTF-8" // otherwise $_POST is empty
            },
        })
        .then(response => response.json())
        .then(json => {
            console.log(json);            
            this.setState({
                data: json,
                totalCount: json.total_results
            })
        })
  }

  render(){
    console.log('App', this.props);
    return (
      <Router>
        <div>
          <Header/>
          <Route path="/" exact  render={(props) => <CardsListMovie {...props}/>} />
          <Route path="/movie/:id" render={(props) => <MoviePage {...props}  />} />
        </div>
      </Router>
    );
  }
}


export default App;
