import React, {Fragment} from 'react';

import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';

const styles = theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-around',
        overflow: 'hidden',
    },
    itemWrapp: {
        display: 'flex',
        justifyContent: 'center',
    },
    itemImg: {
        width: '300px',
        height: '388px',
        textAlign: 'center',
        boxShadow: '0px 0px 18px -6px rgba(0,0,0,0.75)',
        transition: 'all ease .3s',
        backgroundPositionX: 'center',
        backgroundPositionY: 'center',
        backgroundSize: '100%',
        backgroundRepeat: 'no-repeat',
        cursor: 'pointer',
        '&:hover': {
            transition: 'all ease .3s',
            backgroundSize: '150%',
            boxShadow: '0px 0px 18px -6px rgba(0,0,0,1)',
        }
    }
});

class CardMovie extends React.Component {
    render() {
        const { classes } = this.props;
        return (
            <Fragment>
            {
                this.props.movieInfo.map(movie => {
                    return ( 
                        <Grid item xs={12} lg={3} sm={6} md={4} xl={3} className={classes.itemWrapp} key={movie.img}>
                            <div className={classes.itemImg} style={{backgroundImage: "url(" + `https://image.tmdb.org/t/p/w500/${movie.poster_path}` + ")"}}></div>         
                        </Grid>
                    )
                })
            }
            </Fragment>
        )
    }    
}

export default withStyles(styles)(CardMovie);
